import React, { useState, useEffect } from 'react';

function Clock() {
  const [time, setTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(new Date()); // Update the state with the new date every second
    }, 1000);

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []);

  // Function to get a greeting based on the time of day
  const getGreeting = () => {
    const hours = time.getHours();
    if (hours < 12) {
      return 'Good Morning';
    } else if (hours < 18) {
      return 'Good Afternoon';
    } else {
      return 'Good Evening';
    }
  };

  // Function to format the date with weekday and ordinal day
  const formatDate = () => {
    const options = { weekday: 'long', day: 'numeric', month: 'long', year: 'numeric' };
    const date = time.toLocaleDateString('en-US', options);
    const day = time.getDate();
    const suffix = day % 10 === 1 && day !== 11 ? 'st' : 
                   day % 10 === 2 && day !== 12 ? 'nd' : 
                   day % 10 === 3 && day !== 13 ? 'rd' : 'th';
    return date.replace(/\d+(?:st|nd|rd|th)/, `${day}${suffix}`);
  };

  return (
    <div className="clock">
      <h3>{getGreeting()}, :)</h3>
      <h3>How are you today?</h3>
      <h3>It's now {time.toLocaleTimeString('en-US', { hour12: true })}. </h3> {/* Display time */}
      <h3>{formatDate()}.</h3> {/* Display detailed date */}
    </div>
  );
}

export default Clock;
