import React, { useEffect } from 'react';

function GoogleCalendar() {
  useEffect(() => {
    // Load the Google Calendar API and set up the client
    const loadCalendar = () => {
      window.gapi.load('client:auth2', () => {
        window.gapi.client.init({
          apiKey: 'YOUR_API_KEY',  // Replace with your Google API key
          clientId: 'YOUR_CLIENT_ID', // Replace with your Google Client ID
          discoveryDocs: ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest'],
          scope: 'https://www.googleapis.com/auth/calendar.readonly',
        }).then(() => {
          window.gapi.auth2.getAuthInstance().signIn().then(() => {
            listUpcomingEvents();
          });
        });
      });
    };

    const listUpcomingEvents = () => {
      window.gapi.client.calendar.events.list({
        calendarId: 'primary',
        timeMin: (new Date()).toISOString(),
        showDeleted: false,
        singleEvents: true,
        maxResults: 10,
        orderBy: 'startTime',
      }).then((response) => {
        const events = response.result.items;
        if (events.length > 0) {
          console.log('Upcoming events:');
          events.forEach((event) => {
            const when = event.start.dateTime || event.start.date;
            console.log(`${event.summary} (${when})`);
          });
        } else {
          console.log('No upcoming events found.');
        }
      });
    };

    loadCalendar();
  }, []);

  return (
    <div className="google-calendar">
      <h3>Your Google Calendar</h3>
      <div id="calendar-content">
        {/* Calendar events will be listed here */}
      </div>
    </div>
  );
}

export default GoogleCalendar;
